var squishy={squishies:[],init:function(e){var t=$(window).width()/e;squishies=$(".squishyText");$(squishies).each(function(){this.squishFontSize=Math.floor(parseInt($(this).css("font-size").replace("px",""))*t*100)/100;$(this).css("font-size",this.squishFontSize+"px");this.squishFactor=Math.floor($(this).parent().width()/this.squishFontSize*100)/100});$(window).resize(function(){$(squishies).each(squishy.squish)})},squish:function(){var e=$(this).parent().width();var t=Math.floor(e/this.squishFactor*100)/100;$(this).css("font-size",t+"px")}}